    /**
     * @inheritdoc IERC721ABase
     */
    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes calldata _data
    ) public virtual override {
        _safeTransferFrom(from, to, tokenId, _data);
    }
}
