// SPDX-License-Identifier: MIT

pragma solidity 0.8.15;

import {SenderIsNotSelf} from "../../../../../common/Errors.sol";
import "../../../../access/ownable/OwnableInternal.sol";
import "../../base/ERC1155BaseInternal.sol";
import "./IERC1155MintByOwner.sol";

/**
 * @dev Extension of {ERC1155} that allows owner to mint new tokens.
 */
contract ERC1155MintByOwner is
    IERC1155MintByOwner,
    ERC1155BaseInternal,
    OwnableInternal
{
    /**
     * @inheritdoc IERC1155MintByOwner
     */
    function mintByOwner(
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public virtual onlyOwner {
        _mint(to, id, amount, data);
    }
}

contracts/features/access/ownable/Ownabl.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.15;

import {OwnableStorage} from "./OwnableStorage.sol";
import {IERC173Events} from "./IERC173Events.sol";

abstract contract OwnableInternal is IERC173Events {
    using OwnableStorage for OwnableStorage.Layout;

    modifier onlyOwner() {
        require(msg.sender == _owner(), "Ownable: sender must be owner");
        _;
    }

    function _owner() internal view virtual returns (address) {
        return OwnableStorage.layout().owner;
    }

    function _transferOwnership(address account) internal virtual {
        OwnableStorage.layout().setOwner(account);
        emit OwnershipTransferred(msg.sender, account);
    }
}

