    struct Layout {
        string baseURI;
        string fallbackURI;
        bool baseURILocked;
        bool fallbackURILocked;
        uint256 lastLockedTokenId;
        mapping(uint256 => string) tokenURIs;
    }

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }
}

